mapper.update:
UPDATE MEMBER SET
AUTHORITY = 1
WHERE MEMBER_NO = #{hiddenNo}

UPDATE "SELLER" SET
AUTH_DATE = SYSDATE
WHERE MEMBER_NO = #{hiddenNo}

UPDATE MEMBER SET
AUTHORITY = 4
WHERE MEMBER_NO = #{hiddenNo}

mapper.select:
SELECT COUNT(*) 
FROM "SELLER_AUTH"
WHERE MEMBER_NO = #{hiddenNo}

SELECT AUTH_DENY_REASON
FROM SELLER_AUTH
WHERE MEMBER_NO = #{memberNo}

SELECT * FROM (SELECT M.MEMBER_NO, M.MEMBER_ID, M.AUTHORITY, M.MEMBER_DEL_FL, M.MEMBER_NICKNAME, REPORT_NO, REPORT_MEMBER_NO, REPORT_TARGET_NO, REPORT_REASON, REPORT_CONTENT, REPORT_DATE, REPORT_PENALTY, PROCESS_DATE, RANK() OVER(PARTITION BY REPORT_TARGET_NO ORDER BY REPORT_NO DESC) AS RANKING, RANK() OVER(PARTITION BY REPORT_TARGET_NO ORDER BY REPORT_NO) AS REPORT_VOLUME, B.BOARD_NO, BOARD_DEL_FL, POST_NO, POST_DEL_FL, REPORT_TYPE, COMMENT_NO, COMMENT_DEL_FL, (CASE WHEN REPORT_TYPE = 'B' THEN B.BOARD_NO WHEN REPORT_TYPE = 'P' THEN POST_NO WHEN REPORT_TYPE = 'C' THEN COMMENT_NO END ) CONTENT_NO, (CASE WHEN REPORT_TYPE = 'B' THEN BOARD_TITLE WHEN REPORT_TYPE = 'P' THEN POST_TITLE END ) TITLE, (CASE WHEN REPORT_TYPE = 'B' THEN BOARD_CONTENT WHEN REPORT_TYPE = 'P' THEN POST_CONTENT WHEN REPORT_TYPE = 'C' THEN COMMENT_CONTENT END ) CONTENT, (CASE WHEN REPORT_TYPE = 'C' THEN MC.MEMBER_ID END ) COMMENT_MEMBER_ID, (CASE WHEN REPORT_TYPE = 'C' THEN C.BOARD_NO END ) COMMENT_BOARD_NO, (CASE WHEN REPORT_TYPE = 'B' THEN BOARD_TYPE_NO WHEN REPORT_TYPE = 'C' THEN (SELECT BOARD_TYPE_NO FROM BOARD b WHERE b.BOARD_NO = C.BOARD_NO) END ) BOARD_TYPE FROM REPORT R LEFT JOIN BOARD B ON(REPORT_TARGET_NO = BOARD_NO) LEFT JOIN MEMBER M ON(REPORT_TARGET_NO = M.MEMBER_NO) LEFT JOIN POST ON(REPORT_TARGET_NO = POST_NO) LEFT JOIN "COMMENT" C ON(REPORT_TARGET_NO = COMMENT_NO) LEFT JOIN MEMBER MC ON(MC.MEMBER_NO = C.MEMBER_NO)) WHERE REPORT_TYPE = 'M' AND REPORT_TARGET_NO = #{memberNo}
AND REPORT_PENALTY IS NULL
ORDER BY REPORT_NO

SELECT * FROM (SELECT M.MEMBER_NO, M.MEMBER_ID, M.AUTHORITY, M.MEMBER_DEL_FL, M.MEMBER_NICKNAME, REPORT_NO, REPORT_MEMBER_NO, REPORT_TARGET_NO, REPORT_REASON, REPORT_CONTENT, REPORT_DATE, REPORT_PENALTY, PROCESS_DATE, RANK() OVER(PARTITION BY REPORT_TARGET_NO ORDER BY REPORT_NO DESC) AS RANKING, RANK() OVER(PARTITION BY REPORT_TARGET_NO ORDER BY REPORT_NO) AS REPORT_VOLUME, B.BOARD_NO, BOARD_DEL_FL, POST_NO, POST_DEL_FL, REPORT_TYPE, COMMENT_NO, COMMENT_DEL_FL, (CASE WHEN REPORT_TYPE = 'B' THEN B.BOARD_NO WHEN REPORT_TYPE = 'P' THEN POST_NO WHEN REPORT_TYPE = 'C' THEN COMMENT_NO END ) CONTENT_NO, (CASE WHEN REPORT_TYPE = 'B' THEN BOARD_TITLE WHEN REPORT_TYPE = 'P' THEN POST_TITLE END ) TITLE, (CASE WHEN REPORT_TYPE = 'B' THEN BOARD_CONTENT WHEN REPORT_TYPE = 'P' THEN POST_CONTENT WHEN REPORT_TYPE = 'C' THEN COMMENT_CONTENT END ) CONTENT, (CASE WHEN REPORT_TYPE = 'C' THEN MC.MEMBER_ID END ) COMMENT_MEMBER_ID, (CASE WHEN REPORT_TYPE = 'C' THEN C.BOARD_NO END ) COMMENT_BOARD_NO, (CASE WHEN REPORT_TYPE = 'B' THEN BOARD_TYPE_NO WHEN REPORT_TYPE = 'C' THEN (SELECT BOARD_TYPE_NO FROM BOARD b WHERE b.BOARD_NO = C.BOARD_NO) END ) BOARD_TYPE FROM REPORT R LEFT JOIN BOARD B ON(REPORT_TARGET_NO = BOARD_NO) LEFT JOIN MEMBER M ON(REPORT_TARGET_NO = M.MEMBER_NO) LEFT JOIN POST ON(REPORT_TARGET_NO = POST_NO) LEFT JOIN "COMMENT" C ON(REPORT_TARGET_NO = COMMENT_NO) LEFT JOIN MEMBER MC ON(MC.MEMBER_NO = C.MEMBER_NO)) WHERE REPORT_TYPE = #{reportType} AND REPORT_TARGET_NO = #{contentNo}
AND REPORT_PENALTY IS NULL
ORDER BY REPORT_NO

mapper.update:
UPDATE MEMBER SET
MEMBER_DEL_FL = 'Y'
WHERE MEMBER_NO = #{hiddenNo}

UPDATE BOARD SET
BOARD_DEL_FL = 'Y'
WHERE BOARD_NO = #{hiddenContentNo}

UPDATE REPORT SET
REPORT_PENALTY = 'Y',
PROCESS_DATE = SYSDATE
WHERE REPORT_TYPE = #{reportType}
AND REPORT_TARGET_NO = #{hiddenContentNo}

UPDATE REPORT SET
REPORT_PENALTY = 'N',
PROCESS_DATE = SYSDATE
WHERE REPORT_TYPE = #{reportType}
AND REPORT_TARGET_NO = #{hiddenContentNo}
